INSERT INTO `bucketsource` VALUES ('b9c4aa49-e966-438e-b7a7-3308795f7394', '本地存储桶', 'local', '(() => {\r\n	return {\r\n		// 用户视图能看见什么样的表单\r\n		config: [\r\n      {\r\n        \"type\": \"string\",\r\n        \"label\":\"指定存储路径\",\r\n        \"field\": \"path\",\r\n        \"default\": \"\",\r\n        \"required\": true,\r\n        \"placeholder\":\"例如: upload/\"\r\n      },\r\n      {\r\n        \"type\":\"option\",\r\n        \"label\":\"自定义路径\",\r\n        \"field\":\"customPath\",\r\n        \"default\":\"\",\r\n        \"required\":false,\r\n        \"options\": [\r\n          { label: \"年\", value: \"year\" },\r\n          { label: \"年月\", value: \"year-month\" },\r\n          { label: \"年月日\", value: \"year-month-day\" }\r\n        ],\r\n        \"placeholder\":\"请选择自定义路径\"\r\n      },\r\n      {\r\n        \"type\": \"string\",\r\n        \"label\":\"访问前缀\",\r\n        \"field\": \"baseUrl\",\r\n        \"default\": window.uploader_ip,\r\n        \"hidden\":true,\r\n        \"required\": true,\r\n        \"placeholder\":\"例如: upload/\"\r\n      },\r\n    ],\r\n		// 上传配置\r\n		uploader: {\r\n			// 注意：不要使用箭头函数，否则会改变this的指向\r\n			// 前置操作：返回请求配置前的操作，例如数据处理、获取token等操作\r\n			beforeEach: async function () {\r\n				\r\n			},\r\n      getDateDir (type) {\r\n        const date = new Date()\r\n        const year = date.getFullYear()\r\n        const month = date.getMonth() + 1\r\n        const months = month > 10 ? month : \'0\' + month\r\n        const day = date.getDate()\r\n        let str = \'\'\r\n        switch (type) {\r\n          case \'year\':\r\n            str += year + \'/\'\r\n            break\r\n          case \'year-month\':\r\n            str += year + \'/\' + months + \'/\'\r\n            break\r\n          case \'year-month-day\':\r\n            str += year + \'/\' + months + \'/\' + day + \'/\'\r\n            break\r\n        }\r\n        return str\r\n      },\r\n			// 注意：不要使用箭头函数，否则会改变this的指向\r\n			// 需要返回axios的RequestConfig类型的请求配置，参考：https://www.axios-http.cn/docs/req_config\r\n			request: async function (file) {\r\n        const ip = `${window.uploader_ip}/api/v1/tool/upload`\r\n        const customPath = \'${config.customPath}\'\r\n        let path = \'${config.path}\'\r\n        if (customPath) {\r\n          // 判断是否有尾/，没有加上\r\n          path = /\\/$/.test(path) ? path : path + \'/\'\r\n          path += this.getDateDir(customPath)\r\n        }\r\n        const formData = new FormData()\r\n        formData.append(\'file\', file.file)\r\n        formData.append(\'path\', path + file.filename)\r\n				return {\r\n					url: ip,\r\n					method: \"post\",\r\n          data: formData\r\n				}\r\n			},\r\n			// 注意：不要使用箭头函数，否则会改变this的指向\r\n			// 此处res代表axios的response，从res中拿到数据并需返回{ img_url: \"\", hash: \"\" }格式\r\n			response: function (res) {\r\n        const tmp = res.data\r\n				return {\r\n					img_url: tmp.data.img_url,\r\n					hash: tmp.data.hash\r\n				}\r\n			}\r\n		}\r\n	}\r\n})()', '2023-03-07 02:53:45', '2023-07-14 02:27:19', 1, '1.0.1');
